(() => {
	"use strict";
	$(".slider__container").slick({
		dots: !1,
		infinite: !1
	}), $(document).ready((function() {
		$(".product_card").hover((function() {
			var t = $(this).attr("id");
			t = "." + t, $(t).addClass("shown")
		}), (function() {
			var t = $(this).attr("id");
			t = "." + t, $(t).removeClass("shown")
		}))
	}))
})();

